    <FilterItems  boxShadow='0 0 9px 3px #8b8686' width="20%">
       <SelectOptions
                  name="Category"
                  id="Category"
                >
                 {categories?.map((item, index) =>  <option key={index} value={item?.name}>{item?.name}</option> )}
