import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import productService from "../services/network/product.service";
import {
  ProductContainerWrapperMaster,
  ProductContainerWrapper,
  DetailsProductItemImageWrapper,
  ProductItem,
  ProductItemImage,
  ProductWrapper,
} from "../styled-component/Container.styled";
import { ItemDetails } from "../styled-component/Typography.styled";
import { truncateText } from "../utils/utils";
import FilterComponent from "./Shared-Component/Filter";

function ProductContainer(props) {
  const histoy = useHistory()
  const [products, setProducts] = React.useState([]);


  useEffect(() => {
    getProducts();
    return () => {
      setProducts([]); 
    };
  }, []);

  const getProducts = async () => {
    await productService
      .getProductListService()
      .then((resp) => {
        console.log(resp, "resp");
        resp?.message == "Success" && setProducts(resp.products);
      })
      .catch((err) => {
        setProducts([]);
      });
  };


  const productClickHandler =  (product) => {
    console.log(product, "productClickHandler");
    histoy.push({
      pathname : `product/${product._id}`,
      state:  product }
    ) 
  }
  return (
    <ProductContainerWrapperMaster>
      <FilterComponent></FilterComponent>
      <ProductContainerWrapper>
        {products?.map((product, index) => (
          <ProductWrapper onClick={() => productClickHandler(product)} key={index}>
            <ProductItem>
              <DetailsProductItemImageWrapper>
                <ProductItemImage height='' src={product.avatar} alt={product.name}/>
              </DetailsProductItemImageWrapper>
            </ProductItem>
            <ItemDetails alignText="left" title={product.name}>{truncateText(product.name, 23)}</ItemDetails>
            <ItemDetails alignText="center">${product.price}</ItemDetails>
          </ProductWrapper>
        ))}
      </ProductContainerWrapper>
    </ProductContainerWrapperMaster>
  );
}

export default ProductContainer;
