import { apiRequest } from "./api";

const METHOD_GET = "GET";
const METHOD_POST = "POST";
const METHOD_PUT = "PUT";
const METHOD_DELETE = "DELETE";

const GET_PRODUCTS = "https://upayments-studycase-api.herokuapp.com/api/products";
const GET_PRODUCTS_BY_ID = "https://upayments-studycase-api.herokuapp.com/api/products";
const CREATE_PRODUCTS = "https://upayments-studycase-api.herokuapp.com/api/products";
const CATEGORY_LIST = "https://upayments-studycase-api.herokuapp.com/api/categories";



/* @request url, method ,@return {Object} */

const getProductListService = () => {
    return apiRequest({
        method: METHOD_GET,
        url: GET_PRODUCTS,
    });
}


const getProductByIdService = (id) => {
    return apiRequest({
        method: METHOD_GET,
        url: `${GET_PRODUCTS}/${id}`,
    });
}

const createProductService = (data) => {
    return apiRequest({
        method: METHOD_POST,
        url: CREATE_PRODUCTS,
        data
    });
}


const getCategoryService = () => {
    return apiRequest({
        method: METHOD_GET,
        url: CATEGORY_LIST,
    });
}




const ProductService = {
    getProductListService,
    getProductByIdService,
    createProductService,
    getCategoryService
};

export default ProductService;
