import styled from 'styled-components'
const windowWidth = window.screen.width
export const DashboardPageLayout = styled.div `
position: static;
    height: 100vh;
    background-color: #cdcdcd;
    display: flex;
    justify-content: center;
`
export const DashboardContentWrapper = styled.div `
width: calc(${windowWidth}px - 20%);
background-color: #cdcdcd;
justify-content: space-around;
`

export const ProductContainerWrapperMaster = styled.div `
padding: 1rem;
`
export const ProductContainerWrapper = styled.div `
display: grid;
grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
grid-gap: 1rem;
width: 70%;
    margin:3rem auto 0rem auto;

`

export const ProductItem = styled.div `
font-size: 5vw;
padding: .5em;
background: gold;
text-align: center;
`
export const ProductItemImage = styled.div `
background: url(${props=> props.url});
padding: .5em;
// background: gold;
`
