import React, { useEffect } from "react";
import productService from "../services/network/product.service";
import {
  ProductContainerWrapperMaster,
  ProductContainerWrapper,
  ProductItemImageWrapper,
  ProductItem,
  ProductItemImage,
  ProductWrapper,
} from "../styled-component/Container.styled";
import { ItemDetails } from "../styled-component/Typography.styled";
import FilterComponent from "./Shared-Component/Filter";

function ProductContainer() {
  const [products, setProducts] = React.useState([]);

  useEffect(() => {
    getProducts();
    return () => {
      setProducts([]); // This worked for me
    };
  }, []);

  const getProducts = async () => {
    await productService
      .getProductListService()
      .then((resp) => {
        console.log(resp, "resp");
        resp?.message == "Success" && setProducts(resp.products);
      })
      .catch((err) => {
        setProducts([]);
      });
  };

  return (
    <ProductContainerWrapperMaster>
      <FilterComponent></FilterComponent>
      <ProductContainerWrapper>
        {products?.map((product, index) => (
          <ProductWrapper key={index}>
            <ProductItem>
              <ProductItemImageWrapper>
                <ProductItemImage src={product.avatar}></ProductItemImage>
              </ProductItemImageWrapper>
            </ProductItem>
            <ItemDetails alignText="left">{product.name}</ItemDetails>
            <ItemDetails alignText="right">${product.price}</ItemDetails>
          </ProductWrapper>
        ))}
      </ProductContainerWrapper>
    </ProductContainerWrapperMaster>
  );
}

export default ProductContainer;
