import React, { useEffect } from "react";
import { useParams, useRouteMatch } from "react-router-dom";
import productService from "../services/network/product.service";
import {
  ProductContainerWrapperMaster,
  ProductContainerWrapper,
  ProductItemImageWrapper,
  ProductItem,
  ProductItemImage,
  ProductWrapper,
} from "../styled-component/Container.styled";
import { ProductDetailItem, ProductDetailWrapper } from "../styled-component/Flex.styled";
import { ItemDetails } from "../styled-component/Typography.styled";
import FilterComponent from "./Shared-Component/Filter";

function ProductContainerDetails() {

  const [productsInfo, setproductsInfo] = React.useState({});
  const {id} = useParams()
  const {path} = useRouteMatch()

  console.log(id, path)


  useEffect(() => {
    getProductById();
    return () => {
      setproductsInfo([]); // This worked for me
    };
  }, [id]);


  const getProductById = async () => {
    console.log("id", id);
    await productService.getProductByIdService(id)
      .then((resp) => {
      console.log(resp, "resp");
        resp?.message == "Success" && setproductsInfo(resp.product);
      })
      .catch((err) => {
        setproductsInfo([]);
      });
  };


  return (

        <ProductDetailWrapper>
          <ProductDetailItem width="30%">
          <ProductItemImageWrapper>
                <ProductItemImage src={productsInfo?.avatar} alt={productsInfo?.name}/>
              </ProductItemImageWrapper>
          </ProductDetailItem>
          <ProductDetailItem width="70%"></ProductDetailItem>
        </ProductDetailWrapper>
  );
} 

export default ProductContainerDetails;
